mapper.update:
UPDATE broker
SET update_time = CURRENT_TIMESTAMP
WHERE 1 = 1

  AND address = #{brokerAddress}



    AND id = #{brokerId}

mapper.select:
SELECT id, cluster_name AS clusterName, broker_name AS brokerName, broker_id AS brokerId,
address, version, dc, update_time AS updateTime
FROM broker
WHERE 1 = 1

    AND cluster_name = #{clusterName}



    AND broker_name = #{brokerName}



    AND broker_id = #{brokerId}



    AND dc = #{dc}




    AND update_time >= #{updateTime}

SELECT b.dc AS dcId , b.id AS brokerId , IFNULL(SUM(tbx.read_queue_num ), 0) AS readQueueNum , IFNULL(SUM(tbx.write_queue_num), 0) AS writeQueueNum FROM broker AS b LEFT JOIN topic_broker_xref AS tbx ON b.id = tbx.broker_id LEFT JOIN topic AS t ON tbx.topic_id = t.id WHERE 1 = 1
AND b.dc = #{dcId}
AND b.broker_id = #{brokerId}
GROUP BY b.dc, b.id ORDER BY write_queue_num, read_queue_num ASC

SELECT COUNT(1) > 0
FROM broker_consumer_group_xref AS xref
WHERE xref.broker_id = #{brokerId}
AND xref.consumer_group_id = #{consumerGroupId}

SELECT COUNT(1) > 0
FROM topic_broker_xref
WHERE broker_id = #{brokerId}
AND topic_id = #{topicId}
