mapper.select:
SELECT id, cluster_name AS clusterName, broker_name AS brokerName, broker_id AS brokerId,
address, version, dc, last_update_time AS lastUpdateTime
FROM broker
WHERE 1 = 1

    AND cluster_name = #{clusterName}



    AND broker_name = #{brokerName}



    AND broker_id = #{brokerId}



    AND dc = #{dc}




    AND last_update_time >= #{lastUpdateTime}

SELECT broker.dc AS dcId , broker.id AS brokerId , IFNULL(SUM(topic.read_queue_num ), 0) AS readQueueNum , IFNULL(SUM(topic.write_queue_num), 0) AS writeQueueNum FROM broker LEFT JOIN topic_broker_xref ON broker.id = topic_broker_xref.broker_id LEFT JOIN topic ON topic_broker_xref.topic_id = topic.id WHERE 1 = 1
AND broker.dc = #{dcId}
GROUP BY broker.dc, broker.id ORDER BY write_queue_num, read_queue_num ASC
